package com.amit.javaPractise;

import java.util.Objects;

/*
Problem : You are given a decimal number N.

Task : Print YES if the given number can be represented in the form of an integer, else print NO.

Example:

Input N = "35.8" Output = NO
As 35.8 can not be accurately represented as an integer as 35 is not equal to 35.8, the answer is NO.
 */

public class CheckStringIsInteger {
    public static void main(String[] args) {
        String s = ".";
        System.out.println(isInteger(s));

    }

    public static boolean isInteger(String N) {
        int i = -1;
        i = N.indexOf(".");
        if (i == -1) {
            return true;
        } else {
            //With For loop
            /*
            for (int j = i + 1; j < N.length(); j++) {
                if (N.charAt(j) != '0') {
                    return false;
                }
            }
            */

            //Without for loop
            try {
                if (!Objects.equals(Integer.parseInt(N.substring(i + 1)), 0)) {
                    return false;
                }
            } catch (Exception e) {
                return false;
            }
        }
        return true;
    }
}

